mapper.insert:
		/* ====== kr.co.gocle.ifx.service.impl.CmsIfxMapper.insertCourseContentSummary ====== */
		INSERT 
			  INTO CMS_COURSE_CONTENT_SUMMARY 
			       ( 
			          CMS_COURSE_SUMMARY_SEQ
			         ,TITLE 
			         ,INSTITUTION_ID 
			         ,COURSE_CODE
			         ,IS_OPERATING 
			         ,DEVICE_TYPE_CODE 
			         ,ID 
			         ,STUDY_TIME_IN_HOURS 
			         ,IS_AVAILABLE 
			         ,SUBTITLE 
			         ,REGISTERED_DATE
       				 ,LAST_MODIFIED_DATE
			         ,INSERT_DATE 
			       ) 
			       VALUES 
			       ( 
			         (SELECT NVL(MAX(CMS_COURSE_SUMMARY_SEQ),0)+1 FROM CMS_COURSE_CONTENT_SUMMARY) 
			         ,#{title} 
			         ,#{institution_id} 
			         ,#{course_code}  
			         ,#{is_operating}   
			         ,#{registered_from_device_type_code}
			         ,#{id}
			         ,#{study_time_in_hours}  
			         ,#{is_available}
			         ,#{subtitle} 
			         ,#{registered_date} 
			         ,#{last_modified_date} 
			         ,SYSDATE  
			       )

mapper.select:
/* ====== kr.co.gocle.ifx.service.impl.CmsIfxMapper.listCourseContentSummary ====== */
select cms_course_summary_seq, title, institution_id, course_code as courseCode, is_operating, device_type_code, id, SUBSTR(course_code,2,4) as year, study_time_in_hours, is_available, subtitle, registered_date as registeredDate, last_modified_date, insert_date , case when SUBSTR(course_code,1,1) = 'A' THEN '평생능력' ELSE '코리아텍' end institutionName, case when device_type_code = '1' THEN 'PC+M' WHEN device_type_code = '2' THEN 'PC+M' WHEN device_type_code = '3' THEN 'PC+M' WHEN device_type_code = '4' THEN 'PC' WHEN device_type_code = '5' THEN '모바일' ELSE '기타' END deviceName, CASE WHEN SUBSTR(COURSE_CODE,13,2) = '08' THEN '디자인콘텐츠' WHEN SUBSTR(COURSE_CODE,13,2) = '14' THEN '건축/설계' WHEN SUBSTR(COURSE_CODE,13,2) = '15' THEN '기계' WHEN SUBSTR(COURSE_CODE,13,2) = '16' THEN '재료' WHEN SUBSTR(COURSE_CODE,13,2) = '17' THEN '화학' WHEN SUBSTR(COURSE_CODE,13,2) = '19' THEN '전기전자' WHEN SUBSTR(COURSE_CODE,13,2) = '20' THEN '정보통신' WHEN SUBSTR(COURSE_CODE,13,2) = '30' THEN '메카트로닉스' WHEN device_type_code = '40' THEN '직업기초' WHEN device_type_code = '50' THEN '공통과목' ELSE '기타과목' END cateName FROM CMS_COURSE_CONTENT_SUMMARY WHERE ((( LOWER(COURSE_CODE) like '%life%' OR LOWER(COURSE_CODE) like '%free%' OR LOWER(COURSE_CODE) like '%bank%' ) AND SUBTITLE LIKE '%학부%' )
OR( course_code='b55a1fbf-84b0-4b67-9efc-62740a4ab47b' AND SUBTITLE LIKE '%학부%' ) )
AND SUBSTR(COURSE_CODE,2,4) = #{searchYear}
AND SUBSTR(COURSE_CODE,1,1) = #{searchInstitutionId}
AND SUBSTR(COURSE_CODE,1,1) <> 'A'
AND SUBTITLE LIKE '%' || #{searchWrd} || '%'
AND SUBSTR(REGISTERED_DATE,1,10) >= #{searchStDate}
AND SUBSTR(REGISTERED_DATE,1,10) <= #{searchEdDate}
ORDER BY cms_course_summary_seq
