/* ====== kr.co.gocle.oklms.lu.activity.service.impl.ActivityMapper.getActivityMember ====== */ SELECT 'N' as ADDYN ,WEEK.WEEK_CNT ,TO_CHAR(TO_DATE(WEET.TRANING_DATE,'YYYY.MM.DD'), 'dy', 'NLS_DATE_LANGUAGE=korean') AS DAY_OF_WEEK ,WEET.TRANING_DATE ,WEET.TRANING_ST_HOUR ||':'|| WEET.TRANING_ST_MIN as TRANING_ST ,WEET.TRANING_ED_HOUR ||':'|| WEET.TRANING_ED_MIN as TRANING_ED
,CASE WHEN SUBJ.SUBJECT_TRANING_TYPE = 'OJT' THEN WEET.LEAD_TIME ELSE ROUND((SUBJ.LCTRE_TIME+SUBJ.TEST_EXEC_TIME) * 15 / ( SELECT COUNT(*) FROM LMS_SUBJ_WEEK WEEK WHERE DELETE_YN = 'N' AND WEEK.YYYY = SUBJ.YYYY AND WEEK.TERM = SUBJ.TERM AND WEEK.SUBJECT_CODE = SUBJ.SUBJECT_CODE AND WEEK.CLASS = SUBJ.CLASS )) END TIME_HOUR
,MOD(TRIM(WEET.TRANING_ED_MIN), TRIM(WEET.TRANING_ST_MIN) ) AS TIME_MIN ,WEET.TRANING_ST_HOUR ,WEET.TRANING_ST_MIN ,WEET.TRANING_ED_HOUR ,WEET.TRANING_ED_MIN ,WEET.WEEK_ID ,WEET.TIME_ID ,WEET.REVIEW ,WEET.CLASS AS CLASS_ID ,AA.PLAN_TIME ,AA.STUDY_TIME ,AA.ACHIEVEMENT ,CM.MEM_NAME AS MEM_NM ,CM.MEM_ID from lms_subj_week WEEK
INNER JOIN lms_subject SUBJ ON SUBJ.YYYY = WEEK.YYYY AND SUBJ.TERM = WEEK.TERM AND SUBJ.SUBJECT_CODE = WEEK.SUBJECT_CODE AND SUBJ.CLASS = WEEK.CLASS AND SUBJ.DELETE_YN = 'N'
LEFT JOIN lms_subj_week_time WEET ON WEEK.SUBJECT_CODE = WEET.SUBJECT_CODE AND WEET.DELETE_YN = 'N' AND WEEK.WEEK_ID = WEET.WEEK_ID LEFT OUTER JOIN (SELECT TNM.STUDY_DATE, TNM.WEEK_CNT,TND.ADDYN,TND.MEM_ID,TND.MEM_NM,TND.PLAN_TIME,TND.STUDY_TIME,TND.ACHIEVEMENT ,TNM.SUBJECT_CODE,TNM.YYYY,TNM.CLASS FROM LMS_TRANING_NOTE_MASTER TNM,LMS_TRANING_NOTE_DETAIL TND WHERE TNM.TRANINING_NOTE_MASTER_ID = TND.TRANINING_NOTE_MASTER_ID AND TNM.DELETE_YN='N' AND TND.DELETE_YN='N' AND TNM.ADDYN='N') AA ON
AA.MEM_ID=#{memId} AND AA.SUBJECT_CODE = WEEK.SUBJECT_CODE AND AA.WEEK_CNT = WEEK.WEEK_CNT AND AA.YYYY= WEEK.YYYY AND AA.CLASS = WEEK.CLASS AND AA.STUDY_DATE = WEET.TRANING_DATE LEFT OUTER JOIN COM_MEMBER CM ON CM.MEM_ID = #{memId} WHERE WEEK.SUBJECT_CODE = #{subjectCode} AND WEEK.WEEK_CNT =#{weekCnt} AND WEEK.DELETE_YN ='N' AND WEEK.YYYY= #{yyyy} AND WEEK.CLASS = #{classId}
UNION ALL
SELECT 'Y' as ADDYN ,WEEK.WEEK_CNT ,TO_CHAR(TO_DATE(SWE.TRANING_DATE,'YYYY.MM.DD'), 'dy', 'NLS_DATE_LANGUAGE=korean') AS DAY_OF_WEEK ,SWE.TRANING_DATE TRANING_DATE
,SWE.TRANING_ST_HOUR ||':'|| SWE.TRANING_ST_MIN as TRANING_ST ,SWE.TRANING_ED_HOUR ||':'|| SWE.TRANING_ED_MIN as TRANING_ED
,MOD(TRIM(SWE.TRANING_ED_HOUR) ,TRIM(SWE.TRANING_ST_HOUR)) AS TIME_HOUR ,NVL(MOD(NVL(TRIM(SWE.TRANING_ED_MIN),0), NVL(TRIM(SWE.TRANING_ST_MIN),0 )),0) AS TIME_MIN ,SWE.TRANING_ST_HOUR ,LPAD(SWE.TRANING_ST_MIN,2,'0') TRANING_ST_MIN ,SWE.TRANING_ED_HOUR ,LPAD(SWE.TRANING_ED_MIN,2,'0')TRANING_ED_MIN ,SWE.WEEK_ID WEEK_ID ,SWE.TIME_ID TIME_ID ,SWE.REVIEW REVIEW ,SWE.CLASS AS CLASS_ID ,AA.PLAN_TIME ,AA.STUDY_TIME ,AA.ACHIEVEMENT ,CM.MEM_NAME AS MEM_NM ,CM.MEM_ID from lms_subj_week WEEK INNER JOIN lms_subj_week_time_enrichment SWE ON SWE.WEEK_ID =WEEK.WEEK_ID AND SWE.DELETE_YN='N' LEFT OUTER JOIN (SELECT TNM.WEEK_CNT,TND.ADDYN,TND.MEM_ID,TND.MEM_NM,TND.PLAN_TIME,TND.STUDY_TIME,TND.ACHIEVEMENT ,TNM.SUBJECT_CODE,TNM.YYYY,TNM.CLASS FROM LMS_TRANING_NOTE_MASTER TNM,LMS_TRANING_NOTE_DETAIL TND WHERE TNM.TRANINING_NOTE_MASTER_ID = TND.TRANINING_NOTE_MASTER_ID AND TNM.DELETE_YN='N' AND TND.DELETE_YN='N' AND TNM.ADDYN='Y' ) AA ON AA.MEM_ID=#{memId} AND AA.SUBJECT_CODE = WEEK.SUBJECT_CODE AND AA.WEEK_CNT = WEEK.WEEK_CNT AND AA.YYYY= WEEK.YYYY AND AA.CLASS = WEEK.CLASS LEFT OUTER JOIN COM_MEMBER CM ON CM.MEM_ID = #{memId} WHERE WEEK.SUBJECT_CODE = #{subjectCode} AND WEEK.WEEK_CNT = #{weekCnt} AND WEEK.DELETE_YN ='N' AND WEEK.YYYY= #{yyyy} AND WEEK.CLASS = #{classId}

