/* ====== kr.co.gocle.oklms.lu.activity.service.impl.ActivityMapper.getSubjWeek ====== */ SELECT (SELECT MIN(LSWT.TRANING_DATE) FROM LMS_SUBJ_WEEK_TIME LSWT WHERE LSWT.DELETE_YN = 'N' AND SUBJ.YYYY = LSWT.YYYY AND SUBJ.TERM = LSWT.TERM AND SUBJ.SUBJECT_CODE = LSWT.SUBJECT_CODE AND SUBJ.CLASS = LSWT.CLASS ) AS TRANING_ST_DATE , (SELECT MAX(LSWT.TRANING_DATE) FROM LMS_SUBJ_WEEK_TIME LSWT WHERE LSWT.DELETE_YN = 'N' AND SUBJ.YYYY = LSWT.YYYY AND SUBJ.TERM = LSWT.TERM AND SUBJ.SUBJECT_CODE = LSWT.SUBJECT_CODE AND SUBJ.CLASS = LSWT.CLASS ) AS TRANING_END_DATE , (SELECT SUM( SWT.LEAD_TIME ) FROM LMS_SUBJ_WEEK_TIME SWT WHERE SWT.DELETE_YN = 'N' AND SUBJ.YYYY = SWT.YYYY AND SUBJ.TERM = SWT.TERM AND SUBJ.SUBJECT_CODE = SWT.SUBJECT_CODE AND SUBJ.CLASS = SWT.CLASS AND WEK.WEEK_ID = SWT.WEEK_ID ) AS weekTimeHour, CASE WHEN SUBJ.SUBJECT_TRANING_TYPE = 'OJT' THEN (SELECT LISTAGG(NCS_UNIT_NAME, ',') WITHIN GROUP ( ORDER BY NCS_UNIT_NAME) FROM (SELECT DISTINCT NCS_UNIT_NAME, WEEK_ID FROM LMS_SUBJ_WEEK_TIME ) A WHERE WEEK_ID = WEK.WEEK_ID ) ELSE UNIT.NCS_UNIT_NAME END NCS_UNIT_NAME, CASE WHEN SUBJ.SUBJECT_TRANING_TYPE = 'OJT' THEN SWT.NCS_ELEM_NAME ELSE ELEM.NCS_ELEM_NAME END NCS_ELEM_NAME,
CASE WHEN SUBJ.NCS_YN = 'N' THEN ( SELECT LISTAGG(LRN.LESSON_CN, ',') WITHIN GROUP (ORDER BY LRN.LESSON_CN) FROM KUT_HAK.VH_OKLMS_TH_STUDY_LRN_CN LRN WHERE LRN.ESTBL_YY = WEK.YYYY AND DECODE(LRN.ESTBL_SEMSTR_CD,'101','1','102','2','103','3','4') = WEK.TERM AND LRN.COURSE_NO = WEK.SUBJECT_CODE AND LRN.PARTITN_CLAS_SE_CD = WEK.CLASS AND LRN.WEEK_NUM = WEK.WEEK_CNT
) ELSE ( NVL( (SELECT LISTAGG(LRN.LESSON_CN, ',') WITHIN GROUP (ORDER BY LRN.LESSON_CN) FROM KUT_MOB2.VH_OKLMS_TH_STUDY_LRN_NCS LRN WHERE LRN.ESTBL_YY = WEK.YYYY AND DECODE(LRN.ESTBL_SEMSTR_CD,'101','1','102','2','103','3','4') = WEK.TERM AND LRN.COURSE_NO = WEK.SUBJECT_CODE AND LRN.PARTITN_CLAS_SE_CD = WEK.CLASS AND LRN.WEEK_NUM = WEK.WEEK_CNT AND ROWNUM < 11 ), (SELECT LISTAGG(LRN.LESSON_CN, ',') WITHIN GROUP (ORDER BY LRN.LESSON_CN) FROM TEMP_OKLMS_TH_STUDY_LRN_NCS LRN WHERE LRN.ESTBL_YY = WEK.YYYY AND DECODE(LRN.ESTBL_SEMSTR_CD,'101','1','102','2','103','3','4') = WEK.TERM AND LRN.COURSE_NO = WEK.SUBJECT_CODE AND LRN.PARTITN_CLAS_SE_CD = WEK.CLASS AND LRN.WEEK_NUM = WEK.WEEK_CNT AND ROWNUM < 11 ) )
) END LESSON_CN,
SUBJ.traning_hour , WEK.WEEK_ID , SUBJ.SUBJECT_NAME , SUBJ.CLASS AS CLASS_ID , SUBJ.YYYY, SUBJ.SUBJECT_CODE , SUBJ.SUBJECT_TRANING_TYPE , SUBJ.SUBJECT_TYPE , SUBJ.TERM, WEK.WEEK_CNT, LTPM.COMPANY_ID, CASE WHEN SUBJ.SUBJECT_TRANING_TYPE = 'OFF' THEN FLOOR((SUBJ.LCTRE_TIME+SUBJ.TEST_EXEC_TIME) * 15 / FLOOR ( (SELECT COUNT(*) FROM LMS_SUBJ_WEEK WEEK WHERE DELETE_YN = 'N' AND WEEK.YYYY = SUBJ.YYYY AND WEEK.TERM = SUBJ.TERM AND WEEK.SUBJECT_CODE = SUBJ.SUBJECT_CODE AND WEEK.CLASS = SUBJ.CLASS ) ) ) ELSE 0 END OFF_TIME_HOUR, FN_GETCOMPAYNAME(LTPM.COMPANY_ID) AS COMPANY_NAME FROM LMS_SUBJECT SUBJ INNER JOIN LMS_SUBJ_WEEK WEK ON SUBJ.SUBJECT_CODE = WEK.SUBJECT_CODE AND SUBJ.CLASS = WEK.CLASS AND WEK.DELETE_YN ='N' AND WEK.YYYY = SUBJ.YYYY AND WEK.TERM = SUBJ.TERM LEFT JOIN LMS_SUBJ_WEEK_TIME SWT ON SWT.DELETE_YN = 'N' AND SUBJ.YYYY = SWT.YYYY AND SUBJ.TERM = SWT.TERM AND SUBJ.SUBJECT_CODE = SWT.SUBJECT_CODE AND SUBJ.CLASS = SWT.CLASS AND WEK.WEEK_ID = SWT.WEEK_ID LEFT JOIN lms_subj_ncs_unit UNIT ON SUBJ.SUBJECT_CODE = UNIT.SUBJECT_CODE AND UNIT.DELETE_YN ='N' AND WEK.WEEK_ID = UNIT.WEEK_ID AND SUBJ.YYYY = UNIT.YYYY AND SUBJ.TERM = UNIT.TERM LEFT JOIN lms_subj_ncs_elem ELEM ON SUBJ.SUBJECT_CODE = ELEM.SUBJECT_CODE AND ELEM.DELETE_YN ='N' AND WEK.WEEK_ID = ELEM.WEEK_ID AND SUBJ.YYYY = ELEM.YYYY AND SUBJ.TERM = ELEM.TERM LEFT OUTER JOIN LMS_TRANING_PROCESS_MAPPING LTPM ON LTPM.DELETE_YN='N' AND SUBJ.YYYY = LTPM.YYYY AND SUBJ.TERM = LTPM.TERM AND SUBJ.SUBJECT_CODE = LTPM.SUBJECT_CODE AND SUBJ.CLASS = LTPM.CLASS WHERE SUBJ.DELETE_YN ='N' AND SUBJ.YYYY =#{yyyy} AND SUBJ.TERM = #{term}
AND SUBJ.SUBJECT_CODE = #{subjectCode}
AND WEK.WEEK_CNT =#{weekCnt}
AND SUBJ.CLASS = #{classId}
AND ROWNUM = 1

/* ====== kr.co.gocle.oklms.lu.activity.service.impl.ActivityMapper.listActivityMember ====== */
SELECT CM.MEM_NAME ,CM.MEM_SEQ ,CM.MEM_ID ,CM.OUT_YN ,FN_GETCOMPAYNAME(CM.COMPANY_ID) AS COMPANY_NAME ,LL.LESSON_ID ,LAN.CONTENT ,LAN.REQ_CONTENT ,LAN.ATCH_FILE_ID ,AF.ORG_FILE_NAME ,NVL(AA.STUDY_TIME,'0') as STUDY_TIME FROM LMS_LESSON LL INNER JOIN COM_MEMBER CM ON LL.MEM_SEQ=CM.MEM_SEQ AND CM.DELETE_YN='N' LEFT OUTER JOIN LMS_ACTIVITY_NOTE LAN ON LL.YYYY = LAN.YYYY AND LL.TERM = LAN.TERM AND LL.SUBJECT_CODE = LAN.SUBJECT_CODE AND LL.CLASS = LAN.CLASS AND LAN.MEM_ID= CM.MEM_ID AND LAN.DELETE_YN='N' AND LAN.WEEK_CNT = #{weekCnt} LEFT OUTER JOIN ATCH_FILE AF ON AF.ATCH_FILE_ID= LAN.ATCH_FILE_ID
LEFT OUTER JOIN (SELECT TND.MEM_ID,TND.MEM_NM, SUM(TND.STUDY_TIME) AS STUDY_TIME ,TNM.SUBJECT_CODE,TNM.YYYY,TNM.CLASS FROM LMS_TRANING_NOTE_MASTER TNM,LMS_TRANING_NOTE_DETAIL TND WHERE TNM.TRANINING_NOTE_MASTER_ID = TND.TRANINING_NOTE_MASTER_ID AND TNM.DELETE_YN='N' AND TND.DELETE_YN='N' AND TNM.WEEK_CNT = #{weekCnt} AND TNM.SUBJECT_CODE= #{subjectCode} GROUP BY TND.MEM_ID,TND.MEM_NM, TNM.SUBJECT_CODE,TNM.YYYY,TNM.CLASS
) AA
ON AA.MEM_ID=CM.MEM_ID AND AA.YYYY= LL.YYYY AND AA.CLASS = LL.CLASS
WHERE LL.DELETE_YN='N' AND LL.YYYY = #{yyyy} AND LL.TERM = #{term} AND LL.SUBJECT_CODE = #{subjectCode} AND LL.CLASS = #{classId}
AND CM.MEM_NAME LIKE '%' || #{searchName } || '%'
ORDER BY CM.MEM_NAME

 			/* ====== kr.co.gocle.oklms.lu.activity.service.impl.ActivityMapper.listActivityOjtMember ====== */

 			SELECT CM.MEM_NAME
			 			,CM.MEM_SEQ
						,CM.MEM_ID
						,FN_GETCOMPAYNAME(CM.COMPANY_ID) AS COMPANY_NAME
						,LL.LESSON_ID 

						,NVL(AA.STUDY_TIME,'0') as STUDY_TIME
			 FROM LMS_LESSON LL 
			 INNER JOIN COM_MEMBER CM  ON  LL.MEM_SEQ=CM.MEM_SEQ  AND CM.DELETE_YN='N' 


			LEFT OUTER JOIN (SELECT TND.MEM_ID,TND.MEM_NM, SUM(TND.STUDY_TIME) AS STUDY_TIME ,TNM.SUBJECT_CODE,TNM.YYYY,TNM.CLASS
									FROM LMS_TRANING_NOTE_MASTER TNM,LMS_TRANING_NOTE_DETAIL TND
									WHERE TNM.TRANINING_NOTE_MASTER_ID = TND.TRANINING_NOTE_MASTER_ID
									AND TNM.DELETE_YN='N' AND TND.DELETE_YN='N'
									AND TNM.WEEK_CNT =  #{weekCnt}
									AND TNM.SUBJECT_CODE= #{subjectCode}
									GROUP BY  TND.MEM_ID,TND.MEM_NM, TNM.SUBJECT_CODE,TNM.YYYY,TNM.CLASS

									) AA 

									ON  AA.MEM_ID=CM.MEM_ID    AND AA.YYYY=  LL.YYYY   AND AA.CLASS  = LL.CLASS 

			 WHERE 	LL.DELETE_YN='N'
				AND LL.YYYY = #{yyyy}
				AND LL.TERM = #{term}
				AND LL.SUBJECT_CODE = #{subjectCode}
				AND LL.CLASS = #{classId}

			AND    CM.MEM_NAME  LIKE '%' || #{searchName  } || '%'  


			AND    CM.MEM_ID  = #{searchId}



 			ORDER BY CM.MEM_NAME

 			/* ====== kr.co.gocle.oklms.lu.activity.service.impl.ActivityMapper.listLessonMember ====== */

 			SELECT CM.MEM_NAME
			 			,CM.MEM_SEQ
						,CM.MEM_ID
						,FN_GETCOMPAYNAME(CM.COMPANY_ID) AS COMPANY_NAME
			 FROM LMS_LESSON LL 
			 INNER JOIN COM_MEMBER CM  ON  LL.MEM_SEQ=CM.MEM_SEQ  AND CM.DELETE_YN='N' 


			 WHERE 	LL.DELETE_YN='N'
				AND LL.YYYY = #{yyyy}
				AND LL.TERM = #{term}
				AND LL.SUBJECT_CODE = #{subjectCode}
				AND LL.CLASS = #{classId}


 			ORDER BY CM.MEM_NAME

/* ====== kr.co.gocle.oklms.lu.activity.service.impl.ActivityMapper.listOjtActivityApproval ====== */ SELECT ACT.ACTIVITY_NOTE_ID, ACT.STATUS, ACT.CONTENT, ACT.RETURN_COMMENT, TO_CHAR(ACT.INSERT_DATE,'YYYY.MM.DD') AS INSERT_DATE, LS.SUBJECT_NAME, LS.YYYY, LS.TERM, LS.SUBJECT_CODE, LESS.CLASS AS SUBJECT_CLASS, FN_GETCOMPAYNAME(MEMB.COMPANY_ID) AS COMPANY_NAME , WEEK.WEEK_CNT , WEET.NCS_UNIT_NAME, LESS.LESSON_ID, MEMB.MEM_NAME, MEMB.MEM_ID, MEMB.MEM_SEQ , MEMB.COMPANY_ID FROM LMS_LESSON LESS INNER JOIN LMS_SUBJ_WEEK WEEK ON LESS.SUBJECT_CODE = WEEK.SUBJECT_CODE AND WEEK.YYYY = LESS.YYYY AND WEEK.TERM = LESS.TERM AND WEEK.CLASS = LESS.CLASS AND WEEK.DELETE_YN = 'N' INNER JOIN LMS_SUBJ_WEEK_TIME WEET ON LESS.SUBJECT_CODE = WEET.SUBJECT_CODE AND WEET.YYYY = LESS.YYYY AND WEET.TERM = LESS.TERM AND WEET.CLASS = LESS.CLASS AND WEET.WEEK_ID = WEEK.WEEK_ID AND WEET.DELETE_YN = 'N' INNER JOIN COM_MEMBER MEMB ON LESS.MEM_SEQ = MEMB.MEM_SEQ AND MEMB.DELETE_YN='N' INNER JOIN LMS_ACTIVITY_NOTE ACT ON ACT.YYYY = LESS.YYYY AND ACT.TERM = LESS.TERM AND ACT.SUBJECT_CODE = LESS.SUBJECT_CODE AND ACT.CLASS = LESS.CLASS AND ACT.DELETE_YN = 'N' AND ACT.TIME_ID = WEET.TIME_ID AND ACT.MEM_ID = MEMB.MEM_ID
AND ACT.STATUS = #{actSearchStatus}
INNER JOIN LMS_SUBJECT LS ON LS.YYYY = LESS.YYYY AND LS.TERM = LESS.TERM AND LS.SUBJECT_CODE = LESS.SUBJECT_CODE AND LS.CLASS = LESS.CLASS AND LS.DELETE_YN = 'N' AND LS.SUBJECT_TRANING_TYPE = 'OJT' AND LS.SUBJECT_TYPE = 'NORMAL' INNER JOIN LMS_SUBJ_INS_MAPPING INS ON INS.YYYY = LESS.YYYY AND INS.TERM = LESS.TERM AND INS.SUBJECT_CODE = LESS.SUBJECT_CODE AND INS.CLASS = LESS.CLASS AND INS.DELETE_YN = 'N' AND INS.MEM_SEQ = #{sessionMemSeq} WHERE LESS.DELETE_YN = 'N' AND LESS.YYYY = #{yyyy} AND LESS.TERM = #{term}
AND LESS.SUBJECT_CODE = #{searchSubjectCode}
ORDER BY ACT.ACTIVITY_NOTE_ID DESC
