mapper.select:
		/* ====== kr.co.gocle.oklms.lu.attend.service.impl.AttendMapper.getProgressId ====== */
		SELECT DISTINCT PROGRESS_ATTEND_ID
			      FROM LMS_PROGRESS_ATTEND
		 WHERE LESSON_ID = #{stdLessonId}
           AND WEEK_ID  = #{weekId} 
           AND SUBJ_SCH_ID = #{subjSchId}

		/* ====== kr.co.gocle.oklms.lu.attend.service.impl.AttendMapper.getCmsProgressId ====== */
		SELECT DISTINCT PROGRESS_ATTEND_ID
			      FROM LMS_PROGRESS_ATTEND
		 WHERE LESSON_ID = #{stdLessonId}
           AND WEEK_ID  = #{weekId} 
           AND SUBJ_SCH_ID = #{subjSchId}
           AND CMS_LESSON_ID = #{cmsLessonId}
           AND CMS_LESSON_ITEM_ID = #{cmsLessonItemId}
           AND CMS_LESSON_SUB_ITEM = #{cmsLessonSubItem}

/* ====== kr.co.gocle.oklms.lu.attend.service.impl.AttendMapper.getTimeCount ====== */
SELECT COUNT(1)
	     FROM LMS_LESSON_TIME
  WHERE LESSON_TIME_ID = #{progressTimeId}

/* ====== kr.co.gocle.oklms.lu.attend.service.impl.AttendMapper.getTimeId ====== */
SELECT MAX(LESSON_TIME_ID)
	     FROM LMS_LESSON_TIME
  WHERE PROGRESS_ATTEND_ID = #{progressAttendId}

mapper.update:
/* ====== kr.co.gocle.oklms.lu.attend.service.impl.AttendMapper.updateTime ====== */
UPDATE LMS_LESSON_TIME 
       SET LESSON_END_TIME = #{vDateTime},
       UPDATE_USER = #{updateUser},
       UPDATE_DATE = SYSDATE 
 WHERE LESSON_TIME_ID = #{progressTimeId}

mapper.select:
/* ====== kr.co.gocle.oklms.lu.attend.service.impl.AttendMapper.getTimeMaxCount ====== */
SELECT COUNT(1)
	     FROM LMS_LESSON_TIME_MAX
  WHERE PROGRESS_ATTEND_ID = #{progressAttendId}

/* ====== kr.co.gocle.oklms.lu.attend.service.impl.AttendMapper.getTimeMaxId ====== */
SELECT COUNT(1)
	     FROM LMS_LESSON_TIME_MAX
  WHERE PROGRESS_ATTEND_ID = #{progressAttendId}

mapper.update:
		/* ====== kr.co.gocle.oklms.lu.attend.service.impl.AttendMapper.updateTimeMax ====== */




         UPDATE LMS_LESSON_TIME_MAX
			    SET (LESSON_START_TIME, LESSON_END_TIME, LESSON_TIME) =
                (SELECT   MIN (LESSON_START_TIME), MAX (LESSON_END_TIME),
                        SUM(LESSON_END_TIME - LESSON_START_TIME)
                    FROM LMS_LESSON_TIME
                   WHERE PROGRESS_ATTEND_ID = #{progressAttendId}
                GROUP BY PROGRESS_ATTEND_ID)
       WHERE PROGRESS_ATTEND_ID = #{progressAttendId}

mapper.select:
/* ====== kr.co.gocle.oklms.lu.attend.service.impl.AttendMapper.getPageProgressCount ====== */
SELECT COUNT(1)
	     FROM LMS_NON_PROGRESS_ATTEND
  WHERE PROGRESS_ATTEND_ID = #{progressAttendId}
     AND PAGE_INFO = #{pageInfo}

/* ====== kr.co.gocle.oklms.lu.attend.service.impl.AttendMapper.getPageProgressSumCount ====== */
SELECT COUNT(1)
	     FROM LMS_NON_PROGRESS_ATTEND_SUM
  WHERE PROGRESS_ATTEND_ID = #{progressAttendId}

		/* ====== kr.co.gocle.oklms.lu.attend.service.impl.AttendMapper.getPageProgressInfo ====== */
		 SELECT SUM('100') AS PROGRESS_SUM,
		 		COUNT(PAGE_INFO) AS PROGRESS_COUNT
        FROM (
              SELECT PROGRESS_ATTEND_ID, PAGE_INFO
                FROM LMS_NON_PROGRESS_ATTEND
               WHERE PROGRESS_YN = 'Y'
                 AND PROGRESS_ATTEND_ID = #{progressAttendId}
             ) ATT
       GROUP BY PROGRESS_ATTEND_ID

mapper.update:
		/* ====== kr.co.gocle.oklms.lu.attend.service.impl.AttendMapper.updatePageProgressSum ====== */
		UPDATE LMS_NON_PROGRESS_ATTEND_SUM
		   SET PROGRESS_SUM = #{progressSum},
                PROGRESS_COUNT= #{progressCount}
          WHERE PROGRESS_ATTEND_ID = #{progressAttendId}

/* ====== kr.co.gocle.oklms.lu.attend.service.impl.AttendMapper.updateTimeProgress ====== */
UPDATE LMS_PROGRESS_ATTEND
	SET TIME_PROGRESS_RATIO = #{timeProgressRatio},
		UPDATE_USER = #{updateUser},
    	UPDATE_DATE  = SYSDATE  
	WHERE PROGRESS_ATTEND_ID = #{progressAttendId}

mapper.select:
	/* ====== kr.co.gocle.oklms.lu.attend.service.impl.AttendMapper.getLessonProgress ====== */
	  SELECT 



                	FLOOR(PAGE_PROGRESS_RATIO / (100 * 1) * 100)


                	FLOOR(TIME_PROGRESS_RATIO / (100 * 1) * 100)


            		FLOOR((TIME_PROGRESS_RATIO+ATTE.PAGE_PROGRESS_RATIO) / (200 * 1) * 100)  



		  FROM 
		       (SELECT SUM(TIME_PROGRESS_RATIO) AS TIME_PROGRESS_RATIO, 
		              SUM(PAGE_PROGRESS_RATIO) AS PAGE_PROGRESS_RATIO 
		         FROM LMS_PROGRESS_ATTEND ATT 
		        WHERE ATT.LESSON_ID = #{stdLessonId} 
		              AND ATT.WEEK_ID = #{weekId} 
		              AND ATT.SUBJ_SCH_ID = #{subjSchId} 
		        GROUP BY ATT.LESSON_ID, 
		              ATT.WEEK_ID, 
		              ATT.SUBJ_SCH_ID
		       ) ATTE

	/* ====== kr.co.gocle.oklms.lu.attend.service.impl.AttendMapper.getLessonProgress ====== */
	  SELECT 



                	FLOOR(PAGE_PROGRESS_RATIO / (100 * 1) * 100)


                	FLOOR(TIME_PROGRESS_RATIO / (100 * 1) * 100)


            		FLOOR((TIME_PROGRESS_RATIO+ATTE.PAGE_PROGRESS_RATIO) / (200 * 1) * 100)  



		  FROM 
		       (SELECT SUM(TIME_PROGRESS_RATIO) AS TIME_PROGRESS_RATIO, 
		              SUM(PAGE_PROGRESS_RATIO) AS PAGE_PROGRESS_RATIO 
		         FROM LMS_PROGRESS_ATTEND ATT 
		        WHERE ATT.LESSON_ID = #{stdLessonId} 
		              AND ATT.WEEK_ID = #{weekId} 
		              AND ATT.SUBJ_SCH_ID = #{subjSchId} 
		        GROUP BY ATT.LESSON_ID, 
		              ATT.WEEK_ID, 
		              ATT.SUBJ_SCH_ID
		       ) ATTE

	/* ====== kr.co.gocle.oklms.lu.attend.service.impl.AttendMapper.getWeekProgressRate ====== */




                	SELECT FLOOR(PAGE_PROGRESS_RATIO / (100 * (SELECT SUM(PAGE_COUNT) FROM LMS_SUBJ_WEEK_SCH WHERE DELETE_YN = 'N' AND WEEK_ID = #{weekId} ) ) * 100) AS WEEK_PROGRESS_RATE 
		            FROM 
		                  (SELECT SUM(TIME_PROGRESS_RATIO) AS TIME_PROGRESS_RATIO, 
		                         SUM(PAGE_PROGRESS_RATIO) AS PAGE_PROGRESS_RATIO 
		                    FROM LMS_PROGRESS_ATTEND ATT 
		                   WHERE ATT.LESSON_ID = #{stdLessonId}   
		                         AND ATT.WEEK_ID = #{weekId} 
		                     GROUP BY ATT.LESSON_ID, 
		                         ATT.WEEK_ID 
		                  ) ATTE


                	 SELECT FLOOR(TIME_PROGRESS_RATIO / (100 * (SELECT SUM(PAGE_COUNT) FROM LMS_SUBJ_WEEK_SCH WHERE DELETE_YN = 'N' AND WEEK_ID = #{weekId} ) ) * 100) AS WEEK_PROGRESS_RATE 
		              FROM 
		                   (SELECT SUM(TIME_PROGRESS_RATIO) AS TIME_PROGRESS_RATIO, 
		                          SUM(PAGE_PROGRESS_RATIO) AS PAGE_PROGRESS_RATIO 
		                     FROM LMS_PROGRESS_ATTEND ATT 
		                    WHERE ATT.LESSON_ID = #{stdLessonId}   
		                          AND ATT.WEEK_ID = #{weekId} 
		                      GROUP BY ATT.LESSON_ID, 
		                          ATT.WEEK_ID 
		                   ) ATTE


            	SELECT FLOOR((TIME_PROGRESS_RATIO+ATTE.PAGE_PROGRESS_RATIO) / (200 * (SELECT SUM(PAGE_COUNT) FROM LMS_SUBJ_WEEK_SCH WHERE DELETE_YN = 'N' AND WEEK_ID = #{weekId} ) ) * 100) AS WEEK_PROGRESS_RATE 
	              FROM 
	                   (SELECT SUM(TIME_PROGRESS_RATIO) AS TIME_PROGRESS_RATIO, 
	                          SUM(PAGE_PROGRESS_RATIO) AS PAGE_PROGRESS_RATIO 
	                     FROM LMS_PROGRESS_ATTEND ATT 
	                    WHERE ATT.LESSON_ID = #{stdLessonId}  
	                          AND ATT.WEEK_ID = #{weekId} 
	                      GROUP BY ATT.LESSON_ID, 
	                          ATT.WEEK_ID 
	                   ) ATTE

/* ====== kr.co.gocle.oklms.lu.attend.service.impl.AttendMapper.getWeekProgressRateCnt ====== */
  SELECT COUNT(*) FROM LMS_WEEK_ON_LESSON
  	WHERE LESSON_ID = #{stdLessonId} 
  		AND WEEK_ID = #{weekId}

mapper.insert:
/* ====== kr.co.gocle.oklms.lu.attend.service.impl.AttendMapper.insertWeekProgressRate ====== */ INSERT INTO LMS_WEEK_ON_LESSON ( LESSON_ID, WEEK_ID, WEEK_ON_PROGRESS_RATE ) SELECT LES.LESSON_ID, WEE.WEEK_ID, (
SELECT FLOOR(PAGE_PROGRESS_RATIO / (100 * (SELECT SUM(PAGE_COUNT) FROM LMS_SUBJ_WEEK_SCH WHERE DELETE_YN = 'N' AND WEEK_ID = #{weekId} ) ) * 100) FROM (SELECT SUM(TIME_PROGRESS_RATIO) AS TIME_PROGRESS_RATIO, SUM(PAGE_PROGRESS_RATIO) AS PAGE_PROGRESS_RATIO FROM LMS_PROGRESS_ATTEND ATT WHERE ATT.LESSON_ID = #{stdLessonId} AND ATT.WEEK_ID = #{weekId} GROUP BY ATT.LESSON_ID, ATT.WEEK_ID ) ATTE
SELECT FLOOR(TIME_PROGRESS_RATIO / (100 * (SELECT SUM(PAGE_COUNT) FROM LMS_SUBJ_WEEK_SCH WHERE DELETE_YN = 'N' AND WEEK_ID = #{weekId} ) ) * 100) FROM (SELECT SUM(TIME_PROGRESS_RATIO) AS TIME_PROGRESS_RATIO, SUM(PAGE_PROGRESS_RATIO) AS PAGE_PROGRESS_RATIO FROM LMS_PROGRESS_ATTEND ATT WHERE ATT.LESSON_ID = #{stdLessonId} AND ATT.WEEK_ID = #{weekId} GROUP BY ATT.LESSON_ID, ATT.WEEK_ID ) ATTE
SELECT FLOOR((TIME_PROGRESS_RATIO+ATTE.PAGE_PROGRESS_RATIO) / (200 * (SELECT SUM(PAGE_COUNT) FROM LMS_SUBJ_WEEK_SCH WHERE DELETE_YN = 'N' AND WEEK_ID = #{weekId} ) ) * 100) FROM (SELECT SUM(TIME_PROGRESS_RATIO) AS TIME_PROGRESS_RATIO, SUM(PAGE_PROGRESS_RATIO) AS PAGE_PROGRESS_RATIO FROM LMS_PROGRESS_ATTEND ATT WHERE ATT.LESSON_ID = #{stdLessonId} AND ATT.WEEK_ID = #{weekId} GROUP BY ATT.LESSON_ID, ATT.WEEK_ID ) ATTE
)
FROM LMS_LESSON LES INNER JOIN LMS_SUBJ_WEEK WEE ON LES.YYYY = WEE.YYYY AND LES.TERM = WEE.TERM AND LES.SUBJECT_CODE = WEE.SUBJECT_CODE AND LES.CLASS = WEE.CLASS AND WEE.DELETE_YN = 'N' AND WEE.WEEK_ID = #{weekId} WHERE LES.LESSON_ID = #{stdLessonId}

mapper.update:
	/* ====== kr.co.gocle.oklms.lu.attend.service.impl.AttendMapper.updateWeekProgressRate ====== */



		 UPDATE LMS_WEEK_ON_LESSON OLES 
		       SET OLES.WEEK_ON_PROGRESS_RATE = 
		       (




                	SELECT FLOOR(PAGE_PROGRESS_RATIO / (100 * (SELECT SUM(PAGE_COUNT) FROM LMS_SUBJ_WEEK_SCH WHERE DELETE_YN = 'N' AND WEEK_ID = #{weekId} ) ) * 100) 
		            FROM 
		                  (SELECT SUM(TIME_PROGRESS_RATIO) AS TIME_PROGRESS_RATIO, 
		                         SUM(PAGE_PROGRESS_RATIO) AS PAGE_PROGRESS_RATIO 
		                    FROM LMS_PROGRESS_ATTEND ATT 
		                   WHERE ATT.LESSON_ID = #{stdLessonId}   
		                         AND ATT.WEEK_ID = #{weekId} 
		                     GROUP BY ATT.LESSON_ID, 
		                         ATT.WEEK_ID 
		                  ) ATTE


                	 SELECT FLOOR(TIME_PROGRESS_RATIO / (100 * (SELECT SUM(PAGE_COUNT) FROM LMS_SUBJ_WEEK_SCH WHERE DELETE_YN = 'N' AND WEEK_ID = #{weekId} ) ) * 100) 
		              FROM 
		                   (SELECT SUM(TIME_PROGRESS_RATIO) AS TIME_PROGRESS_RATIO, 
		                          SUM(PAGE_PROGRESS_RATIO) AS PAGE_PROGRESS_RATIO 
		                     FROM LMS_PROGRESS_ATTEND ATT 
		                    WHERE ATT.LESSON_ID = #{stdLessonId}   
		                          AND ATT.WEEK_ID = #{weekId} 
		                      GROUP BY ATT.LESSON_ID, 
		                          ATT.WEEK_ID 
		                   ) ATTE


            	SELECT FLOOR((TIME_PROGRESS_RATIO+ATTE.PAGE_PROGRESS_RATIO) / (200 * (SELECT SUM(PAGE_COUNT) FROM LMS_SUBJ_WEEK_SCH WHERE DELETE_YN = 'N' AND WEEK_ID = #{weekId} ) ) * 100) 
	              FROM 
	                   (SELECT SUM(TIME_PROGRESS_RATIO) AS TIME_PROGRESS_RATIO, 
	                          SUM(PAGE_PROGRESS_RATIO) AS PAGE_PROGRESS_RATIO 
	                     FROM LMS_PROGRESS_ATTEND ATT 
	                    WHERE ATT.LESSON_ID = #{stdLessonId}  
	                          AND ATT.WEEK_ID = #{weekId} 
	                      GROUP BY ATT.LESSON_ID, 
	                          ATT.WEEK_ID 
	                   ) ATTE





		       ) 
		 WHERE OLES.LESSON_ID = #{stdLessonId} 
		       AND OLES.WEEK_ID = #{weekId}

		/* ====== kr.co.gocle.oklms.lu.attend.service.impl.AttendMapper.updateLessonProgressRate ====== */
		 UPDATE LMS_LESSON SET ON_PROGRESS_RATE = 


		  (




	 	SELECT FLOOR((WEEK_ON_PROGRESS_RATE) / (100 * 
		       (SELECT COUNT(*) 
		         FROM LMS_SUBJ_WEEK 
		        WHERE DELETE_YN = 'N' 
		            AND YYYY = #{yyyy} 
              		AND TERM =#{term} 
              		AND SUBJECT_CODE =#{subjectCode} 
              		AND CLASS =#{subjectClass} 
		       ) ) * 100) 
		  FROM 
		       (SELECT SUM(WEEK_ON_PROGRESS_RATE) AS WEEK_ON_PROGRESS_RATE 
		         FROM LMS_WEEK_ON_LESSON ATT 
		        WHERE ATT.LESSON_ID = #{stdLessonId}  
		       ) ATTE

		  )
		  	WHERE LESSON_ID = #{stdLessonId}
