mapper.select:
/* ====== kr.co.gocle.oklms.lu.currproc.service.impl.CurrProcMapper.listTrainSubjectSeach ====== */
SELECT * FROM (
SELECT DISTINCT tpm.TRANING_PROCESS_MAPPING_ID, tpm.COMPANY_ID, tpm.TRANING_PROCESS_ID, tp.HRD_TRANING_NAME, tpm.YYYY, tpm.TERM, tpm.SUBJECT_CODE, tpm.CLASS as subClass, tpm.DELETE_YN, su.DEPARTMENT_NAME, su.SUBJECT_NAME, su.INSERT_DATE, TO_CHAR (SYSDATE, 'YYYY') NOW_DATE, su.SUBJECT_TRANING_TYPE FROM lms_traning_process_mapping tpm left outer JOIN lms_traning_process tp ON tpm.COMPANY_ID = tp.COMPANY_ID AND tpm.TRANING_PROCESS_ID = tp.TRANING_PROCESS_ID AND tp.DELETE_YN = 'N' inner JOIN lms_subject su ON su.YYYY = tpm.YYYY AND su.TERM = tpm.TERM AND su.SUBJECT_CODE = tpm.SUBJECT_CODE AND su.CLASS = tpm.CLASS AND su.SUBJECT_TRANING_TYPE = 'OJT' and su.DELETE_YN = 'N' where 1 = 1 and tpm.COMPANY_ID = #{companyId}
and tpm.TRANING_PROCESS_ID = #{traningProcessId}
and tpm.DELETE_YN = 'N'
ORDER BY tpm.TRANING_PROCESS_MAPPING_ID ASC
) AA ORDER BY AA.INSERT_DATE DESC

mapper.update:
/* ====== kr.co.gocle.oklms.lu.currproc.service.impl.CurrProcMapper.deleteCotMapping ====== */
	UPDATE LMS_SUBJ_TUT_MAPPING
	SET DELETE_YN = 'Y'
	,UPDATE_USER = #{sessionMemSeq}
	,UPDATE_DATE = SYSDATE
	WHERE YYYY = #{yyyy}
	  and TERM = #{term}
	  and SUBJECT_CODE = #{subjectCode}
	  and CLASS = #{subClass}
	  and MEM_SEQ = #{memSeq}

mapper.select:
    /* ====== kr.co.gocle.oklms.lu.currproc.service.impl.CurrProcMapper.listSubjectClassInfo ====== */


		  SELECT 
		    INSM.YYYY AS yyyy,
		    INSM.TERM AS term,
		    INSM.SUBJECT_CODE AS subjectCode,
		    INSM.CLASS AS subClass
		  FROM LMS_SUBJ_INS_MAPPING INSM 
		 WHERE INSM.SUBJECT_CODE = #{subjectCode}
		       AND INSM.YYYY = #{yyyy}
		       AND INSM.TERM = #{term}
		       AND INSM.MEM_SEQ = #{sessionMemSeq}
		       AND INSM.DELETE_YN='N'

					and INSM.CLASS = #{searchSubClass}

		ORDER BY INSM.CLASS

    /* ====== kr.co.gocle.oklms.lu.currproc.service.impl.CurrProcMapper.getSubjectCnt ====== */
        SELECT
               COUNT(*) CNT
          FROM lms_subject
          WHERE 1=1
          and YYYY = #{yyyy}
		  and TERM = #{term}
		  and SUBJECT_CODE = #{subjectCode}
		  and CLASS = #{subClass}
          and SUBJECT_TRANING_TYPE = 'OJT'
		  and DELETE_YN = 'N'

/* ====== kr.co.gocle.oklms.lu.currproc.service.impl.CurrProcMapper.getMyTrainSubjectInfo ====== */ select sub.SUBJECT_TRANING_TYPE AS subjectTraningType, sub.YYYY AS yyyy, sub.TERM AS term, sub.SUBJECT_CODE AS subjectCode, sub.CLASS AS subClass, sub.DEPARTMENT_NAME AS departmentName, sub.SUBJECT_NAME AS subjectName, sub.GRADE AS grade, sub.POINT_USE_YN AS pointUseYn, CASE WHEN sub.POINT_USE_YN = 'Y' THEN '학점' WHEN sub.POINT_USE_YN = 'N' THEN '비학점' END pointUseYnName, sub.POINT AS point, sub.SUBJECT_TYPE AS subjectType, sub.ONLINE_TYPE AS onlineType,
CASE WHEN sub.ONLINE_TYPE IN ('104','105') THEN '플립러닝' WHEN sub.ONLINE_TYPE = '101' THEN '순수온라인' WHEN sub.ONLINE_TYPE = '102' THEN '브렌디드' WHEN sub.ONLINE_TYPE = '103' THEN '보조활용' ELSE '없음' END onlineTypeName,
CASE WHEN sub.ONLINE_TYPE = 'FLIPPED' THEN '필수' ELSE '미필수' END firstLecLessionAt, (select b.mem_name from lms_subj_ins_mapping a, com_member b where YYYY = #{yyyy} and TERM = #{term} and SUBJECT_CODE = #{subjectCode} and CLASS = #{subClass} and a.MEM_SEQ = b.mem_seq AND ROWNUM = 1 ) memName from lms_subject sub where 1 = 1 and sub.YYYY = #{yyyy} and sub.TERM = #{term} and sub.SUBJECT_CODE = #{subjectCode} and sub.CLASS = #{subClass} and sub.DELETE_YN = 'N'
