/* ====== kr.co.gocle.oklms.lu.planeval.service.impl.PlanEvalMapper.listNcsEvalPlanSubjectRebort ====== */
select a.COMPANY_ID, a.TRANING_PROCESS_ID, FN_GETCOMPAYNAME(a.COMPANY_ID) company_name, a.YYYY, a.TERM, a.SUBJECT_CODE, a.CLASS as subClass, c.SUBJECT_NAME, c.SUBJECT_TRANING_TYPE, b.mem_seq, FN_GETUSERID(b.MEM_SEQ) as mem_id, FN_GETUSERNAME(b.MEM_SEQ) as mem_name from lms_traning_process_mapping a inner join lms_subj_ccm_mapping b on b.COMPANY_ID = a.COMPANY_ID and b.TRANING_PROCESS_ID = a.TRANING_PROCESS_ID and b.MEM_SEQ = #{sessionMemSeq} and b.DELETE_YN = 'N' inner join lms_subject c on c.YYYY = a.YYYY and c.TERM = a.TERM and c.SUBJECT_CODE = a.SUBJECT_CODE and c.CLASS = a.CLASS
and c.SUBJECT_TRANING_TYPE = #{searchSubjectTraningType}
and c.DELETE_YN = 'N' where 1 = 1 and a.DELETE_YN = 'N' and a.COMPANY_ID = #{companyId} and a.TRANING_PROCESS_ID = #{traningProcessId}
and a.yyyy = #{yyyy}
and a.TERM = #{term}
and a.SUBJECT_CODE = #{subjectCode}
and a.CLASS = #{subClass}
order by a.INSERT_DATE desc

/* ====== kr.co.gocle.oklms.lu.planeval.service.impl.PlanEvalMapper.listNcsEvalPlanMemberRebort ====== */
select les.YYYY, les.TERM, les.SUBJECT_CODE, les.CLASS as subClass, FN_GETSUBJECT_NAME(les.YYYY, les.TERM, les.SUBJECT_CODE, les.CLASS) as SUBJECT_NAME, (select SUBJECT_TRANING_TYPE from lms_subject where yyyy= les.YYYY and term = les.TERM and SUBJECT_CODE = les.SUBJECT_CODE and CLASS = les.CLASS and DELETE_YN = 'N' ) as SUBJECT_TRANING_TYPE, les.MEM_SEQ, FN_GETUSERID(les.MEM_SEQ) as mem_id, FN_GETUSERNAME(les.MEM_SEQ) as mem_name from lms_lesson les inner join lms_traning_process_mapping map on map.YYYY = les.YYYY and map.TERM = les.TERM and map.SUBJECT_CODE = les.SUBJECT_CODE and map.CLASS = les.CLASS and map.COMPANY_ID = #{companyId} and map.TRANING_PROCESS_ID = #{traningProcessId} and map.DELETE_YN = 'N' inner join lms_subj_ccm_mapping b on b.COMPANY_ID = map.COMPANY_ID and b.TRANING_PROCESS_ID = map.TRANING_PROCESS_ID and b.MEM_SEQ = #{sessionMemSeq} and b.DELETE_YN = 'N' where 1 = 1 and les.DELETE_YN = 'N'
and (select SUBJECT_TRANING_TYPE from lms_subject where yyyy= les.YYYY and term = les.TERM and SUBJECT_CODE = les.SUBJECT_CODE and CLASS = les.CLASS and DELETE_YN = 'N' ) = #{searchSubjectTraningType}
and les.yyyy = #{yyyy}
and les.TERM = #{term}
and les.SUBJECT_CODE = #{subjectCode}
and les.CLASS = #{subClass}
order by (select SUBJECT_TRANING_TYPE from lms_subject where yyyy= les.YYYY and term = les.TERM and SUBJECT_CODE = les.SUBJECT_CODE and CLASS = les.CLASS and DELETE_YN = 'N' ) asc

