and sc.INSERT_USER IN (SELECT TUT.MEM_SEQ FROM lms_subj_tut_mapping TUT WHERE TUT.YYYY = plan.YYYY AND TUT.TERM = plan.TERM AND TUT.SUBJECT_CODE = plan.SUBJECT_CODE AND TUT.CLASS = plan.CLASS AND TUT.DELETE_YN = 'N' )
