and sc.MEM_SEQ = #{sessionMemSeq}
) as stdScoreCnt, plan.APPLY_APPROVAL_YN, plan.RETUN_REASON, plan.STATUS, plan.ATCH_FILE_ID, (select count(a.atch_file_id) from comtnfiledetail a where a.atch_file_id = plan.ATCH_FILE_ID) as comDetailFileCnt from lms_th_plan_ev plan where plan.YYYY = #{yyyy} and plan.TERM = #{term} and plan.SUBJECT_CODE = #{subjectCode} and plan.CLASS = #{subClass}
and plan.EV_DIV_CD = #{evDivCd}
and plan.INSERT_USER IN (SELECT TUT.MEM_SEQ FROM lms_subj_tut_mapping TUT WHERE TUT.YYYY = plan.YYYY AND TUT.TERM = plan.TERM AND TUT.SUBJECT_CODE = plan.SUBJECT_CODE AND TUT.CLASS = plan.CLASS AND TUT.DELETE_YN = 'N' )
and plan.INSERT_USER = (SELECT LES.MEM_SEQ FROM lms_lesson LES WHERE LES.YYYY = plan.YYYY AND LES.TERM = plan.TERM AND LES.SUBJECT_CODE = plan.SUBJECT_CODE AND LES.CLASS = plan.CLASS and LES.MEM_SEQ = #{memSeq} AND LES.DELETE_YN = 'N' )
and plan.INSERT_USER = #{sessionMemSeq}
and plan.DELETE_YN = 'N' order by plan.EV_DIV_CD asc

