DENSE_RANK() over(order by c.NCS_ELEM_ID,c.PLAN_EV_ID ) as rn, c.PLAN_EV_ID, c.YYYY, c.TERM, c.SUBJECT_CODE, c.CLASS, c.EV_DIV_CD, c.NCS_ELEM_ID, c.NCS_ELEM_NAME, c.LESSON_CN, c.INSERT_USER, b.EVAL_SCORE, b.LESSON_CN_PASS_AT, (select count(LESSON_CN_PASS_AT) from lms_th_first_ev_std_score where 1 = 1 and NCS_ELEM_ID = c.NCS_ELEM_ID and MEM_SEQ = #{memSeq} and INSERT_USER = #{sessionMemSeq} and LESSON_CN_PASS_AT = 'F' and DELETE_YN = 'N' ) AS FAIL_CNT, (select count(LESSON_CN_PASS_AT) from lms_th_first_ev_std_score where 1 = 1 and NCS_ELEM_ID = c.NCS_ELEM_ID and MEM_SEQ = #{memSeq} and INSERT_USER = #{sessionMemSeq} and LESSON_CN_PASS_AT = 'P' and DELETE_YN = 'N' ) AS PASS_CNT FROM (
SELECT a.* FROM lms_th_ncs_plan_lrn a where 1 = 1 and a.YYYY = #{yyyy} and a.TERM = #{term} and a.SUBJECT_CODE = #{subjectCode} and a.CLASS = #{subClass} and a.EV_DIV_CD = #{evDivCd} and a.INSERT_USER = #{sessionMemSeq} and a.DELETE_YN = 'N' ORDER BY a.NCS_ELEM_ID ) c left outer join lms_th_first_ev_std_score b on b.PLAN_EV_ID = c.PLAN_EV_ID AND b.YYYY = c.YYYY AND b.TERM = c.TERM AND b.SUBJECT_CODE = c.SUBJECT_CODE AND b.CLASS = c.CLASS AND b.NCS_ELEM_ID = c.NCS_ELEM_ID AND b.DELETE_YN = 'N' and b.MEM_SEQ = #{memSeq} and b.INSERT_USER = #{sessionMemSeq}

	/* ====== kr.co.gocle.oklms.lu.planeval.service.impl.PlanEvalMapper.listNcsEvalPlanFirstStdScoreRowNumberPrt ====== */
	SELECT

	      DENSE_RANK() over(order by c.NCS_ELEM_ID,c.PLAN_EV_ID ) as rn,
	      c.PLAN_EV_ID,
	      c.YYYY,
	      c.TERM,
	      c.SUBJECT_CODE,
	      c.CLASS,
	      c.EV_DIV_CD,
	      c.NCS_ELEM_ID,
	      c.INSERT_USER,
	      c.MEM_SEQ,
	      c.EVAL_SCORE,
	      c.LESSON_CN_PASS_AT,
	      case when c.EVAL_SCORE is NULL then 'FAIL'
	           when c.EVAL_SCORE  <=  60 then 'FAIL'
	           when c.EVAL_SCORE  >  60 then 'PASS'
	      end as LESSON_CN_PASS
	FROM (


	          SELECT a.*
	   FROM lms_th_first_ev_std_score a
	   where 1 = 1
	    and a.YYYY = #{yyyy}
		and a.TERM = #{term}
		and a.SUBJECT_CODE = #{subjectCode}
		and a.CLASS = #{subClass}
		and a.EV_DIV_CD = #{evDivCd}

        and a.INSERT_USER IN
          (SELECT TUT.MEM_SEQ
         FROM lms_subj_tut_mapping TUT
        WHERE TUT.YYYY = #{yyyy}
          AND TUT.TERM = #{term}
          AND TUT.SUBJECT_CODE = #{subjectCode}
          AND TUT.CLASS = #{subClass}
          AND TUT.DELETE_YN = 'N'
       )


         and a.INSERT_USER =
        (SELECT LES.MEM_SEQ
         FROM lms_lesson LES
        WHERE LES.YYYY = #{yyyy}
          AND LES.TERM = #{term}
          AND LES.SUBJECT_CODE = #{subjectCode}
          AND LES.CLASS = #{subClass}
          AND LES.MEM_SEQ = #{memSeq}
          AND LES.DELETE_YN = 'N'
       )

	    and a.DELETE_YN = 'N'
	   ORDER BY a.NCS_ELEM_ID
	) c

