		      NCS_ELEM_ID,
		      DTL_CD,
		      DTL_CD_NAME
		from lms_th_lctre_ctrl_set
		where 1 = 1
		and YYYY = #{yyyy}
		and TERM = #{term}
		and SUBJECT_CODE = #{subjectCode}
		and CLASS = #{subClass}
		and EV_DIV_CD = #{evDivCd}

        and INSERT_USER IN
          (SELECT TUT.MEM_SEQ
         FROM lms_subj_tut_mapping TUT
        WHERE TUT.YYYY = #{yyyy}
          AND TUT.TERM = #{term}
          AND TUT.SUBJECT_CODE = #{subjectCode}
          AND TUT.CLASS = #{subClass}
          AND TUT.DELETE_YN = 'N'
       )


         and INSERT_USER =
        (SELECT LES.MEM_SEQ
         FROM lms_lesson LES
        WHERE LES.YYYY = #{yyyy}
          AND LES.TERM = #{term}
          AND LES.SUBJECT_CODE = #{subjectCode}
          AND LES.CLASS = #{subClass}
          AND LES.MEM_SEQ = #{memSeq}
          AND LES.DELETE_YN = 'N'
       )

		and DELETE_YN = 'N'

/* ====== kr.co.gocle.oklms.lu.planeval.service.impl.PlanEvalMapper.listNcsEvalPlanFirstCtrlSet ====== */
	select
	      NCS_ELEM_ID,
	      DTL_CD,
	      DTL_CD_NAME
	from lms_th_lctre_ctrl_set
	where 1 = 1
	and YYYY = #{yyyy}
	and TERM = #{term}
	and SUBJECT_CODE = #{subjectCode}
	and CLASS = #{subClass}
	and EV_DIV_CD = #{evDivCd}
	and INSERT_USER = #{sessionMemSeq}
	and DELETE_YN = 'N'


		/* ====== kr.co.gocle.oklms.lu.planeval.service.impl.PlanEvalMapper.getNcsEvalPlanCnt ====== */

        select count(1) as cnt
		  from lms_th_plan_ev
		 where YYYY = #{yyyy}
		   and TERM = #{term}
		   and SUBJECT_CODE = #{subjectCode}
		   and CLASS = #{subClass}
		   and EV_DIV_CD = #{evDivCd}
		   and DELETE_YN = 'N'
		   and INSERT_USER = #{sessionMemSeq}


		/* ====== kr.co.gocle.oklms.lu.planeval.service.impl.PlanEvalMapper.getNcsEvalPlanStdScoreCnt ====== */

        select count(1) as cnt
		  from lms_th_first_ev_std_score
		 where YYYY = #{yyyy}
		   and TERM = #{term}
		   and SUBJECT_CODE = #{subjectCode}
		   and CLASS = #{subClass}
		   and EV_DIV_CD = #{evDivCd}
		   and DELETE_YN = 'N'


		 AND PLAN_EV_ID = #{planEvId}


		 AND NCS_ELEM_ID = #{ncsElemId}





               		AND INSERT_USER = #{sessionMemSeq}
               		AND MEM_SEQ = #{memSeq}


               		AND INSERT_USER = #{sessionMemSeq}
               		AND MEM_SEQ = #{memSeq}


		/* ====== kr.co.gocle.oklms.lu.planeval.service.impl.PlanEvalMapper.getEvDivCd ====== */

        select
       		 plan.EV_DIV_CD,
       		 plan.EV_DIV_NAME
		 from lms_th_plan_ev plan
		 where plan.YYYY = #{yyyy}
		   and plan.TERM = #{term}
		   and plan.SUBJECT_CODE = #{subjectCode}
		   and plan.CLASS = #{subClass}
		   and plan.DELETE_YN = 'N'


		 and plan.EV_DIV_CD = #{evDivCd}






             		and plan.INSERT_USER IN
             		(SELECT TUT.MEM_SEQ
				         FROM lms_subj_tut_mapping TUT
				        WHERE TUT.YYYY = plan.YYYY
				          AND TUT.TERM = plan.TERM
				          AND TUT.SUBJECT_CODE = plan.SUBJECT_CODE
				          AND TUT.CLASS = plan.CLASS
				          AND TUT.DELETE_YN = 'N'
				       )


             		and plan.INSERT_USER =
             		(SELECT LES.MEM_SEQ
				         FROM lms_lesson LES
				        WHERE LES.YYYY = plan.YYYY
				          AND LES.TERM = plan.TERM
				          AND LES.SUBJECT_CODE = plan.SUBJECT_CODE
				          AND LES.CLASS = plan.CLASS
				          AND LES.DELETE_YN = 'N'
				          AND LES.MEM_SEQ = #{memSeq}
				       )




             		and INSERT_USER = #{sessionMemSeq}



	AND ROWNUM = 1
    order by plan.EV_DIV_CD desc

/* ====== kr.co.gocle.oklms.lu.planeval.service.impl.PlanEvalMapper.getLessionMember ====== */
	select
	    les.MEM_SEQ,
	    FN_GETUSERID(les.MEM_SEQ) as memId,
	    FN_GETUSERNAME(les.MEM_SEQ) as memName
	 from lms_lesson les
	WHERE les.YYYY = #{yyyy}
	 AND les.TERM = #{term}
	 AND les.SUBJECT_CODE = #{subjectCode}
	 AND les.CLASS = #{subClass}
	 AND les.DELETE_YN = 'N'

 		AND les.MEM_SEQ = #{memSeq}

 	AND ROWNUM = 1
	order by FN_GETUSERNAME(les.MEM_SEQ) asc

