AND LIE.PLAN_TYPE = #{searchPlanType}
AND LIE.STATUS = #{searchStatus}
AND FN_GETUSERNAME(LIE.STD_MEM_SEQ) LIKE '%' || #{searchWord } || '%'
AND FN_GETUSERID(LIE.STD_MEM_SEQ) LIKE '%' || #{searchWord } || '%'
AND ( FN_GETUSERNAME(LIE.STD_MEM_SEQ) LIKE '%' || #{searchWord } || '%' OR FN_GETUSERID(LIE.STD_MEM_SEQ) LIKE '%' || #{searchWord } || '%' )
ORDER BY LIE.INSERT_DATE DESC

