mapper.select:
/* ======  kr.co.gocle.oklms.lu.report.service.impl.ReportMapper.listLmsSubjWeek ====== */
SELECT WEK.WEEK_ID, 
	       WEK.WEEK_CNT, 
	       WEK.YYYY, 
	       WEK.TERM, 
	       WEK.SUBJECT_CODE, 
	       WEK.CLASS 
	  FROM LMS_SUBJ_WEEK WEK  
	 WHERE WEK.YYYY =  #{yyyy}
	       AND WEK.TERM = #{term}
	       AND WEK.SUBJECT_CODE = #{subjectCode}

	       AND WEK.CLASS =#{subClass}

	       AND WEK.DELETE_YN = 'N' 
	ORDER BY TO_NUMBER(WEK.WEEK_CNT)

/* ====== kr.co.gocle.oklms.lu.report.service.impl.ReportMapper.getReport ====== */ SELECT LS.REPORT_ID, LS.YYYY, LS.TERM, LS.SUBJECT_CODE, LS.CLASS AS SUB_CLASS, LS.WEEK_CNT, LS.REPORT_NAME, LS.REPORT_DESC, LS.EVAL_YN, LS.SCORE, LS.SUBMIT_START_DATE, LS.SUBMIT_START_HOUR, LS.SUBMIT_START_MIN, LS.SUBMIT_END_DATE, LS.SUBMIT_END_HOUR, LS.SUBMIT_END_MIN, LS.ATCH_FILE_ID, LS.DELETE_YN, (SELECT COUNT(1) FROM LMS_REPORT_SUBMIT LRS WHERE LRS.REPORT_ID=LS.REPORT_ID AND LRS.DELETE_YN='N' ) SCORE_CNT,
CASE WHEN TO_CHAR(SYSDATE,'YYYY.MM.DD HH24:MM' ) BETWEEN LS.SUBMIT_START_DATE||' '||LS.SUBMIT_START_HOUR||':'||SUBMIT_START_MIN AND LS.SUBMIT_END_DATE||' '||LS.SUBMIT_END_HOUR||':'||LS.SUBMIT_END_MIN THEN '진행' WHEN TO_CHAR(SYSDATE,'YYYY.MM.DD HH24:MM' ) > LS.SUBMIT_END_DATE||' '||LS.SUBMIT_END_HOUR||':'||LS.SUBMIT_END_MIN THEN '완료' WHEN TO_CHAR(SYSDATE,'YYYY.MM.DD HH24:MM' ) < LS.SUBMIT_START_DATE||' '||LS.SUBMIT_START_HOUR||':'||SUBMIT_START_MIN THEN '예정' ELSE '' END AS SUBMIT_STATUS
FROM LMS_REPORT LS WHERE LS.REPORT_ID = #{reportId} AND LS.DELETE_YN='N'

/* ====== kr.co.gocle.oklms.lu.report.service.impl.ReportMapper.getReportCnt ====== */
SELECT COUNT(1) FROM LMS_REPORT
 WHERE DELETE_YN='N'
  AND YYYY = #{yyyy}
  AND TERM = #{term}
  AND SUBJECT_CODE = 	#{subjectCode}
  AND CLASS =	#{classId}
  AND WEEK_CNT =	#{weekCnt}

mapper.update:
/* ====== kr.co.gocle.oklms.lu.report.service.impl.ReportMapper.updateReportSubmit ====== */ UPDATE LMS_REPORT_SUBMIT SET UPDATE_USER = #{sessionMemSeq},
TITLE=#{title},
CONTENT=#{content},
EVAL_SCORE=#{evalScore},
ATCH_FILE_ID=#{atchFileId},
UPDATE_DATE = SYSDATE WHERE REPORT_SUBMIT_ID = #{reportSubmitId}

/* ====== kr.co.gocle.oklms.lu.report.service.impl.ReportMapper.updateReportFeedBack ====== */ UPDATE LMS_REPORT_FEEDBACK SET UPDATE_USER = #{sessionMemSeq},
REPORT_FEEDBACK_DESC=#{reportFeedbackDesc},
ATCH_FILE_ID=#{atchFileId},
UPDATE_DATE = SYSDATE WHERE REPORT_SUBMIT_ID = #{reportSubmitId}
